import { createWebHashHistory } from 'vue-router'
import { createRouter } from 'vue-router'
import { basicRoutes } from './routes'

// 创建路由器实例
export const router = createRouter({
  history: createWebHashHistory('/'),
  routes: basicRoutes,
  scrollBehavior: () => ({ left: 0, top: 0 }),
})
